%angular

<link rel="stylesheet" href="https://unpkg.com/leaflet@1.0.3/dist/leaflet.css" />
<div id="map" style="height:650px;"> </div>
<script type="text/javascript">

if (window.L) {
    initMap();
} else {
    console.log('Loading Leaflet library');
    var sc = document.createElement('script');
    sc.type = 'text/javascript';
    sc.src = 'https://unpkg.com/leaflet@1.0.3/dist/leaflet.js';
    sc.onload = initMap;
    sc.onerror = function(err) { alert(err); }
    document.getElementsByTagName('head')[0].appendChild(sc);
}



function initMap() {

    var scope = angular.element($("#map").parent('.ng-scope')).scope().compiledScope;
    var map = L.map('map').setView([37.550927, 127.009901], 12);
    L.tileLayer("http://{s}.tile.osm.org/{z}/{x}/{y}.png").addTo(map);
        var myRenderer = L.canvas({ padding: 0.5 });

    console.log(scope.myData)
    for(var i = 0 ; i< 25; i++)
    {
        // draw circles on Lat Long provided 
        L.circleMarker([scope.myData[i][3], scope.myData[i][4]], {
                    renderer : myRenderer,
            radius: scope.myData[i][1] / 1000 ,
            color: myColor(scope.gender,scope.age),
        }).addTo(map);
    }

}

function myColor(gender, age)
{
    if(gender == 1)
    {     
        if(age == 1)
        {    
            return '#2196F3'
        }
        else if(age ==2)
        {
            return '#1766A6'
        }
        else if(age ==3)
        {
            return '#14568C'
        }
        else if(age ==4)
        {
            return '#0E3F66'
        }
        else if(age ==5)
        {
            return '#002A4D'
        }
        else
        {
            return '#071F33'
        }
    }
    else
    {
        if(age == 1)
        {
            return '#F44336'
        }
        else if(age ==2)
        {      
            return '#FF8880'
        }
        else if(age ==3)
        {
            return '#A62C23'
        }
        else if(age ==4)
        {
            return '#8C251D'
        }
        else if(age ==5)
        {
            return '#661B15'
        }
        else
        {
            return '#4D1410'
        }
    }
}
</script>

